import React, { useEffect, useState } from "react";
import Profile from '../Images/Profile.png';
import '../Components/Landing.css';

export default function Landing() {
  const [typedText, setTypedText] = useState("");
  const textToType = [
    "Brett Wallin",
    "Full Stack Software Engineer",
    "I am a junior at Illinois State University majoring in computer science.",
    "I am also a passionate software engineer dedicated to continuous learning and growth."
  ];
  const typingSpeed = 100; // Adjust typing speed (in milliseconds)

  useEffect(() => {
    let currentText = "";
    let currentIndex = 0;

    const interval = setInterval(() => {
      if (currentIndex >= textToType.length) {
        clearInterval(interval);
        return;
      }

      const text = textToType[currentIndex];
      currentText = text.substring(0, currentText.length + 1);
      setTypedText(currentText);

      if (currentText === text) {
        currentIndex++;
      }
    }, typingSpeed);

    return () => {
      clearInterval(interval);
    };
  }, []);

  return (
    <div className="landing">
      <img src={Profile} alt="profile" className="profile-pic" />
      <div className="text-background">
        <h3 className="landing-header">{typedText}</h3>
        <h1 className="landing-header-name">{typedText}</h1>
        <h2 className="landing-job-title">{typedText}</h2>
        <h3 className="landing-description">{typedText}</h3>
        <button className="landing-button">Contact Me</button>
      </div>
    </div>
  );
}
