import React, { useEffect, useState } from "react";
import Profile from '../Images/Profile.png';
import '../Components/Landing.css';

export default function Landing() {
  const [currentText, setCurrentText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const textToType = [
    "Brett Wallin",
    "Full Stack Software Engineer",
    "I am a junior at Illinois State University majoring in computer science.",
    "I am also a passionate software engineer dedicated to continuous learning and growth."
  ];
  const typingSpeed = 100; // Adjust typing speed (in milliseconds)

  useEffect(() => {
    if (currentIndex >= textToType.length) return;

    const interval = setInterval(() => {
      const text = textToType[currentIndex];
      setCurrentText((prevText) =>
        text.substring(0, prevText.length + 1)
      );

      if (currentText === text) {
        clearInterval(interval);
        setCurrentIndex((prevIndex) => prevIndex + 1);
      }
    }, typingSpeed);

    return () => {
      clearInterval(interval);
    };
  }, [currentIndex, currentText]);

  return (
    <div className="landing">
      <img src={Profile} alt="profile" className="profile-pic" />
      <div className="text-background">
        <h3 className="landing-header">Hello, I am</h3>
        <h1 className="landing-header-name">{currentText}</h1>
        {currentIndex === 1 && (
          <h2 className="landing-job-title">{currentText}</h2>
        )}
        {currentIndex === 3 && (
          <h3 className="landing-description">{currentText}</h3>
        )}
        <button className="landing-button">Contact Me</button>
      </div>
    </div>
  );
}
