import React, { useEffect, useRef } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useForm } from '@formspree/react';
import '../Components/Contact.css';

export default function Contact() {
  const [state, handleSubmit] = useForm('xeqbjynn');
  const contactRef = useRef(null);

  const handleFormSubmit = async (values, { setSubmitting, setStatus }) => {
    try {
      if (!values.name || !values.email || !values.message) {
        throw new Error('Please fill out all fields.');
      }

      await handleSubmit(values);
      setStatus({ success: true });
    } catch (error) {
      setStatus({ success: false, error: error.message });
    }
    setSubmitting(false);
  };

  useEffect(() => {
    const contactElement = contactRef.current;
    const scrollHandler = () => {
      const contactTop = contactElement.offsetTop;
      const contactBottom = contactTop + contactElement.offsetHeight;
      const scrollPosition = window.pageYOffset + window.innerHeight;

      if (scrollPosition > contactTop && scrollPosition < contactBottom) {
        contactElement.classList.add('animate-scale-in');
      } else {
        contactElement.classList.remove('animate-scale-in');
      }
    };

    window.addEventListener('scroll', scrollHandler);

    return () => {
      window.removeEventListener('scroll', scrollHandler);
    };
  }, []);

  return (
    <div className='contact' id='contact' ref={contactRef}>
      <h1 className='contact-title'>Contact Me</h1>
      <Formik
        initialValues={{
          name: '',
          email: '',
          message: ''
        }}
        onSubmit={handleFormSubmit}
      >
        {({ isSubmitting, status }) => (
          <Form className='contact-form'>
            {/* form fields */}
          </Form>
        )}
      </Formik>
      {state.succeeded && <p>Thank you for your message!</p>}

      <div className='contact-additional'>
        {/* additional contact information */}
      </div>
    </div>
  );
}
