import React from 'react';
import { useForm, ValidationError } from '@formspree/react';
import '../Components/Contact.css'

export default function Contact() {

  const [state, handleSubmit] = useForm("xeqbjynn");
  if (state.succeeded) {
    return <p>Thanks for contacting me!</p>;
  }

  return (
    <form onSubmit={handleSubmit} className='contact'>
      <h1 className='contact-title'>Contact Me</h1>
      <label htmlFor="email">
        Email Address
      </label>
      <input
        id="name"
        type="name" 
        name="name"
      />
      <ValidationError 
        prefix="Name" 
        field="name"
        errors={state.errors}
      />
      <input
        id="email"
        type="email" 
        name="email"
      />
      <ValidationError 
        prefix="Email" 
        field="email"
        errors={state.errors}
      />
      <textarea
        id="message"
        name="message"
      />
      <ValidationError 
        prefix="Message" 
        field="message"
        errors={state.errors}
      />
      <button type="submit" disabled={state.submitting}>
        Submit
      </button>
    </form>
  );
  
};
