import React, { useEffect, useState } from "react";
import '../Components/Resume.css'

export default function Resume() {
  const [text, setText] = useState("");
  const [resumeContent, setResumeContent] = useState(null);

  useEffect(() => {
    // Fetch the resume content from a JSON file or an API endpoint
    fetchResumeContent()
      .then((data) => setResumeContent(data))
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    if (resumeContent) {
      let currentIndex = 0;
      const intervalId = setInterval(() => {
        if (currentIndex === resumeContent.length) {
          clearInterval(intervalId);
        } else {
          setText(resumeContent.slice(0, currentIndex + 1));
          currentIndex++;
        }
      }, 100);

      return () => {
        clearInterval(intervalId);
      };
    }
  }, [resumeContent]);

  const fetchResumeContent = async () => {
    // Simulate fetching resume content from a JSON file or API endpoint
    const response = await fetch("../Components/Resume.json");
    const data = await response.json();
    return data.resume;
  };

  return (
    <div className="resume">
      <h1 className="resume-title">My Resume</h1>
      <p className="typing-effect">{text}</p>
    </div>
  );
}
