import React, { useEffect, useState } from "react";
import Profile from '../Images/Profile.png';
import '../Components/Landing.css';

export default function Landing() {
  const [currentTextIndex, setCurrentTextIndex] = useState(0);
  const [currentText, setCurrentText] = useState("");
  const textToType = [
    "Hello, I am",
    "Brett Wallin",
    "Full Stack Software Engineer",
    "I am a junior at Illinois State University majoring in computer science.",
    "I am a passionate software engineer dedicated to continuous learning and growth."
  ];
  const typingSpeed = 75; // Adjust typing speed (in milliseconds)
  const cursorSpeed = 500; // Adjust cursor blink speed (in milliseconds)

  useEffect(() => {
    if (currentTextIndex >= textToType.length) {
      return;
    }

    const fullText = textToType[currentTextIndex];
    let currentIndex = 0;

    const interval = setInterval(() => {
      setCurrentText((prevText) => {
        const currentChar = fullText[currentIndex];
        currentIndex++;
        return prevText + currentChar;
      });

      if (currentIndex >= fullText.length) {
        clearInterval(interval);
        setTimeout(() => {
          setCurrentTextIndex((prevIndex) => prevIndex + 1);
          setCurrentText("");
        }, cursorSpeed);
      }
    }, typingSpeed);

    return () => {
      clearInterval(interval);
    };
  }, [currentTextIndex, textToType]);

  useEffect(() => {
    const cursorInterval = setInterval(() => {
      setCurrentText((prevText) => {
        if (prevText.endsWith("|")) {
          return prevText.slice(0, -1);
        } else {
          return prevText + "|";
        }
      });
    }, cursorSpeed);

    return () => {
      clearInterval(cursorInterval);
    };
  }, []);

  return (
    <div className="landing">
      <img src={Profile} alt="profile" className="profile-pic" />
      <div className="text-background">
        <h3 className="landing-header">
          {currentTextIndex >= 0 && currentTextIndex <= 2 && (
            <span>
              {currentText}
            </span>
          )}
        </h3>
        <h1 className="landing-header-name">
          {currentTextIndex === 1 && (
            <span>
              {currentText}
            </span>
          )}
        </h1>
        <h2 className="landing-job-title">
          {currentTextIndex === 2 && (
            <span>
              {currentText}
            </span>
          )}
        </h2>
        <h3 className="landing-description">
          {currentTextIndex === 3 && (
            <span>
              {currentText}
            </span>
          )}
          {currentTextIndex === 4 && (
            <span>
              {currentText}
            </span>
          )}
        </h3>
      </div>
    </div>
  );
}
