import React, { useState } from "react";
import RockPaperScissors from '../Images/RockPaperScissors.jpg';
import TicTacToe from '../Images/TicTacToe.png';
import Portfolio from '../Images/Portfolio.PNG';
import '../Components/Projects.css';

export default function Projects() {
  const projects = [
    {
      image: TicTacToe,
      title: "Tic Tac Toe",
      description: "Tic Tac Toe Game",
      technologies: "Created using Kotlin",
      demoLink: "https://example.com/demo1"
    },
    {
      image: RockPaperScissors,
      title: "Rock Paper Scissors",
      description: "Rock Paper Scissors Game",
      technologies: "Created using Kotlin",
      demoLink: "https://example.com/demo1"
    },
    {
      image: TicTacToe,
      title: "Tic Tac Toe",
      description: "Tic Tac Toe Game",
      technologies: "Created using Kotlin",
      demoLink: "https://example.com/demo1"
    }
   
  ];

  const [currentProjectIndex, setCurrentProjectIndex] = useState(0);

  const showNextProject = () => {
    setCurrentProjectIndex((prevIndex) => (prevIndex + 1) % projects.length);
  };

  const showPreviousProject = () => {
    setCurrentProjectIndex((prevIndex) =>
      (prevIndex - 1 + projects.length) % projects.length
    );
  };

  const renderProject = (project, index) => {
    const isActive = index === currentProjectIndex;
    const isBlurred = Math.abs(index - currentProjectIndex) === 1 || (index === 0 && currentProjectIndex === projects.length - 1) || (index === projects.length - 1 && currentProjectIndex === 0);

    return (
      <div
        key={index}
        className={`project-card ${isActive ? "active" : ""} ${
          isBlurred ? "blur" : ""
        }`}
      >
        <img src={project.image} alt={`project-${index}`} className="project-image" />
        <h3>{project.title}</h3>
        <p>{project.description}</p>
        <p>{project.technologies}</p>
        <a href={project.demoLink}>View Demo</a>
      </div>
    );
  };

  return (
    <div className="project-container">
      <h1 className="projects-title">My Projects</h1>
      <div className="project-cards">
        {projects.map(renderProject)}
      </div>
      <div className="project-navigation">
        <button id="prev-button" onClick={showPreviousProject}>Previous</button>
        <button id="next-button" onClick={showNextProject}>Next</button>
      </div>
    </div>
  );
}
