import React from "react";
import RockPaperScissors from '../Images/RockPaperScissors.jpg'
import TicTacToe from '../Images/TicTacToe.png'
import Portfolio from '../Images/TicTacToe.png'
import '../Components/Projects.css'

export default function Projects() {

    const projectContainer = document.querySelector('.project-container');
    const projectCards = document.querySelectorAll('.project-card');

    let currentIndex = 0;

    function showProject(index) {
        projectCards.forEach((card, i) => {
            if (i === index) {
                card.classList.add('active');
                card.classList.remove('blur');
            } else if (i === index - 1 || i === index + 1) {
                card.classList.remove('active');
                card.classList.add('blur');
            } else {
                card.classList.remove('active');
                card.classList.remove('blur');
            }
        });
    }

    // Example navigation functions
    function showNextProject() {
        currentIndex = (currentIndex + 1) % projectCards.length;
        showProject(currentIndex);
    }

    function showPreviousProject() {
        currentIndex = (currentIndex - 1 + projectCards.length) % projectCards.length;
        showProject(currentIndex);
    }

    // Example event listeners
    const nextButton = document.querySelector('#next-button');
    const prevButton = document.querySelector('#prev-button');

    nextButton.addEventListener('click', showNextProject);
    prevButton.addEventListener('click', showPreviousProject);



    return(
        <div className="project-container">
            <h1 className="projects-title">My Projects</h1>
            <div className="project-cards">
                <div className="project-card active">
                    <img src={TicTacToe} alt="project-one" className="project-image"/>
                    <h3>Tic Tac Toe</h3>
                    <p>Tic Tac Toe Game</p>
                    <p>Created using Kotlin</p>
                    <a>View Demo</a>
                </div>
                <div className="project-card">
                    <img src={RockPaperScissors} alt="project-one" className="project-image"/>
                    <h3></h3>
                    <p></p>
                    <p></p>
                    <a></a>
                </div>
                <div className="project-card">
                    <img src={Portfolio} alt="project-one" className="project-image"/>
                    <h3></h3>
                    <p></p>
                    <p></p>
                    <a></a>
                </div>
            </div>
        </div>
    )
}
